import React, { useState } from 'react';
import './SergitAssistant.css';

const defaultMessages = [
  { from: 'sergit', text: 'Merhaba! Ben SERGIT, size nas覺l yard覺mc覺 olabilirim?' }
];

export default function SergitAssistant() {
  const [open, setOpen] = useState(false);
  const [messages, setMessages] = useState(defaultMessages);
  const [input, setInput] = useState('');

  const handleSend = (e) => {
    e.preventDefault();
    if (!input.trim()) return;
    setMessages([...messages, { from: 'user', text: input }]);
    // Demo cevap
    setTimeout(() => {
      setMessages(msgs => ([...msgs, { from: 'sergit', text: 'SERGIT: Sorunuzu ald覺m, en k覺sa s羹rede yan覺tlayaca覺m!' }]));
    }, 700);
    setInput('');
  };

  return (
    <div className={`sergit-assistant${open ? ' open' : ''}`}> 
      {open ? (
        <div className="sergit-window">
          <div className="sergit-header">
            <span> SERGIT Asistan</span>
            <button className="sergit-close" onClick={() => setOpen(false)}></button>
          </div>
          <div className="sergit-messages">
            {messages.map((msg, i) => (
              <div key={i} className={`sergit-msg ${msg.from}`}>{msg.text}</div>
            ))}
          </div>
          <form className="sergit-input-bar" onSubmit={handleSend}>
            <input
              type="text"
              value={input}
              onChange={e => setInput(e.target.value)}
              placeholder="Mesaj覺n覺z覺 yaz覺n..."
              autoFocus
            />
            <button type="submit">G繹nder</button>
          </form>
        </div>
      ) : (
        <button className="sergit-fab" onClick={() => setOpen(true)}>
          
          <span className="sergit-fab-label">SERGIT</span>
        </button>
      )}
    </div>
  );
}
