import React from 'react';
import { useParams } from 'react-router-dom';
import './ProductDetail.css';

const products = {
  'portabox': {
    name: 'PORTABOX™',
    description: 'Endüstriyel otomasyon sistemleri için geliştirilmiş, yüksek performanslı kontrol ünitesi.',
    features: [
      'Yüksek işlem gücü ve hızlı tepki süresi',
      'Geniş I/O kapasitesi',
      'Modüler yapı ve kolay entegrasyon',
      'Gelişmiş güvenlik özellikleri',
      'Uzaktan izleme ve kontrol imkanı'
    ],
    specifications: {
      'İşlemci': 'ARM Cortex-M7',
      'RAM': '512MB',
      'Depolama': '32GB eMMC',
      'I/O Portları': '24 Dijital, 8 Analog',
      'İletişim': 'Ethernet, RS485, USB, WiFi',
      'Çalışma Sıcaklığı': '-20°C ile +60°C',
      'Güç Girişi': '24V DC'
    },
    image: '/images/portabox.jpg',
    catalog: '/catalogs/portabox-catalog.pdf'
  },
  'vancraft': {
    name: 'VANCRAFT™',
    description: 'Endüstriyel vana kontrol sistemleri için özel tasarlanmış, güvenilir ve verimli çözüm.',
    features: [
      'Akıllı vana kontrol algoritması',
      'Çoklu vana yönetimi',
      'Enerji tasarrufu modu',
      'Otomatik kalibrasyon',
      'Hata teşhis ve raporlama'
    ],
    specifications: {
      'Kontrol Kapasitesi': '16 Vana',
      'Haberleşme': 'Modbus RTU/TCP',
      'Güç Tüketimi': 'Max. 50W',
      'Koruma Sınıfı': 'IP65',
      'Montaj': 'DIN Ray',
      'Çalışma Sıcaklığı': '-10°C ile +50°C',
      'Güç Girişi': '220V AC'
    },
    image: '/images/vancraft.jpg',
    catalog: '/catalogs/Profesyonel Araç İçi Modüler Çözümler Kataloğu.pdf'
  },
  'g-rack': {
    name: 'G-RACK',
    description: 'Endüstriyel tesisler için özel tasarlanmış, yüksek kapasiteli raf sistemleri.',
    features: [
      'Yüksek yük taşıma kapasitesi',
      'Modüler tasarım',
      'Kolay montaj ve sökme',
      'Çeşitli boyut seçenekleri',
      'Dayanıklı malzeme yapısı'
    ],
    specifications: {
      'Yük Kapasitesi': '1000kg/raf',
      'Malzeme': 'Galvanizli Çelik',
      'Raf Derinliği': '600mm, 800mm, 1000mm',
      'Raf Genişliği': '800mm, 1000mm',
      'Raf Yüksekliği': '2000mm, 2500mm, 3000mm',
      'Koruma': 'Toz ve Neme Karşı'
    },
    image: '/images/g-rack.jpg',
    catalog: '/catalogs/g-rack-catalog.pdf'
  }
};

const ProductDetail = () => {
  const { productId } = useParams();
  const product = products[productId];

  const handleCatalogDownload = () => {
    window.open(product.catalog, '_blank');
  };

  const handleQuoteRequest = () => {
    // Teklif formu modalını aç
    alert('Teklif formu yakında eklenecek!');
  };

  if (!product) {
    return (
      <div className="product-not-found">
        <h2>Ürün bulunamadı</h2>
        <p>Aradığınız ürün mevcut değil veya kaldırılmış olabilir.</p>
      </div>
    );
  }

  return (
    <div className="product-detail">
      <div className="product-header">
        <h1>{product.name}</h1>
        <p className="product-description">{product.description}</p>
      </div>

      <div className="product-content">
        <div className="product-image">
          <img src={product.image} alt={product.name} />
        </div>

        <div className="product-info">
          <div className="product-features">
            <h2>Özellikler</h2>
            <ul>
              {product.features.map((feature, index) => (
                <li key={index}>{feature}</li>
              ))}
            </ul>
          </div>

          <div className="product-specifications">
            <h2>Teknik Özellikler</h2>
            <table>
              <tbody>
                {Object.entries(product.specifications).map(([key, value]) => (
                  <tr key={key}>
                    <td>{key}</td>
                    <td>{value}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          <div className="product-cta">
            <button className="btn btn-primary" onClick={handleQuoteRequest}>
              Teklif Al
            </button>
            <button className="btn btn-secondary" onClick={handleCatalogDownload}>
              Katalog İndir
            </button>
          </div>
        </div>
      </div>

      <div className="product-related">
        <h2>İlgili Ürünler</h2>
        <div className="related-products">
          {Object.entries(products)
            .filter(([id]) => id !== productId)
            .map(([id, relatedProduct]) => (
              <div key={id} className="related-product-card">
                <img src={relatedProduct.image} alt={relatedProduct.name} />
                <h3>{relatedProduct.name}</h3>
                <p>{relatedProduct.description}</p>
                <button className="btn btn-outline">Detayları Gör</button>
              </div>
            ))}
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
