import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import ProductDetail from './components/ProductDetail';
import Products from './Products';
import './App.css';
import SergitAssistant from './SergitAssistant';

function App() {
  return (
    <Router>
      <div className="App">
        <header className="header">
          <nav className="nav">
            <Link to="/" className="logo">
              <svg width="200" height="60" viewBox="0 0 200 60" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M20 10h160v40H20z" fill="#1E5BC6"/>
                <text x="100" y="35" textAnchor="middle" fill="white" fontSize="24" fontWeight="bold">G TECHNIC</text>
              </svg>
            </Link>
            <div className="nav-links">
              <Link to="/">Ana Sayfa</Link>
              <Link to="/about">Hakkımızda</Link>
              <Link to="/products">Ürünler</Link>
              <Link to="/contact">İletişim</Link>
            </div>
          </nav>
        </header>

        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/products" element={<Products />} />
          <Route path="/products/:productId" element={<ProductDetail />} />
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
        </Routes>

        <footer className="footer">
          <p>&copy; 2024 G TECHNIC Industrial Solutions. Tüm hakları saklıdır.</p>
        </footer>
        <SergitAssistant />
      </div>
    </Router>
  );
}

function Home() {
  const [selectedProduct, setSelectedProduct] = useState(null);

  const products = {
    'g-tower': {
      name: 'G‑TOWER™',
      description: 'Akıllı Dikey Otomatik Raf Sistemleri. Yerli üretim, ileri teknoloji ve güvenilir çözümler.',
      image: '/images/g-tower.jpg',
      features: [
        '5 ana model: MINI, SLIM, CORE, TWIN, HEAVY',
        'Dikey depolama ile maksimum alan verimliliği',
        'Yüksek taşıma kapasitesi ve hızlı erişim'
      ]
    },
    'g-mezz': {
      name: 'G‑MEZZ™',
      description: 'Yeni Nesil Çok Katlı Depolama Sistemleri. Endüstriyel depolama alanınızı maksimum verimlilik ve akıllı operasyon merkezi haline getirin.',
      image: '/images/g-mezz.jpg',
      features: [
        'Çift yapı avantajı: ResinDek ve sac zemin seçenekleri',
        'AGV/ASRS ve robotik sistemlerle tam uyum',
        'Modüler, genişletilebilir ve özelleştirilebilir platformlar'
      ]
    },
    'portabox': {
      name: 'PORTABOX™',
      description: 'Endüstriyel otomasyon sistemleri için geliştirilmiş, yüksek performanslı kontrol ünitesi.',
      image: '/images/portabox.jpg',
      features: [
        'Yüksek işlem gücü ve hızlı tepki süresi',
        'Geniş I/O kapasitesi',
        'Modüler yapı ve kolay entegrasyon'
      ]
    },
    'vancraft': {
      name: 'VANCRAFT™',
      description: 'Endüstriyel vana kontrol sistemleri için özel tasarlanmış, güvenilir ve verimli çözüm.',
      image: '/images/vancraft.jpg',
      features: [
        'Akıllı vana kontrol algoritması',
        'Çoklu vana yönetimi',
        'Enerji tasarrufu modu'
      ]
    }
  };

  return (
    <main className="home-page">
      <div className="products-sidebar">
        <h2>Ürünlerimiz</h2>
        <ul className="product-list">
          {Object.entries(products).map(([id, product]) => (
            <li
              key={id}
              className={`product-item ${selectedProduct === id ? 'active' : ''}`}
              onClick={() => setSelectedProduct(id)}
            >
              {product.name}
            </li>
          ))}
        </ul>
      </div>
      <div className="product-preview">
        {selectedProduct ? (
          <div className="preview-content horizontal-preview">
            <div className="preview-image horizontal-image">
              <img src={products[selectedProduct].image} alt={products[selectedProduct].name} />
            </div>
            <div className="horizontal-info">
              <h1>{products[selectedProduct].name}</h1>
              <div className="horizontal-details">
                <div className="horizontal-desc">{products[selectedProduct].description}</div>
                <ul className="horizontal-features">
                  {products[selectedProduct].features.map((feature, index) => (
                    <li key={index}>{feature}</li>
                  ))}
                </ul>
              </div>
              <div className="preview-actions">
                <Link to={`/products/${selectedProduct}`} className="btn btn-primary">
                  <span>Detaylı Bilgi</span>
                  <img src="/sergit-icon.svg" alt="SERGİT" className="sergit-icon" />
                </Link>
                <button className="btn btn-secondary" onClick={() => alert('Teklif formu yakında eklenecek!')}>
                  <span>Hızlı Teklif Al</span>
                  <i className="fas fa-bolt"></i>
                </button>
              </div>
            </div>
          </div>
        ) : (
          <div className="preview-placeholder">
            <h2>Ürün Seçin</h2>
            <p>Detaylı bilgi için sol menüden bir ürün seçin.</p>
          </div>
        )}
      </div>
    </main>
  );
}

function About() {
  return (
    <div className="about-page">
      <h1>Hakkımızda</h1>
      <p>G TECHNIC olarak, endüstriyel otomasyon ve teknoloji çözümlerinde 20 yılı aşkın deneyimimizle sektörün öncü firmalarından biriyiz.</p>
    </div>
  );
}

function Contact() {
  return (
    <div className="contact-page">
      <h1>İletişim</h1>
      <form className="contact-form">
        <input type="text" placeholder="Adınız" />
        <input type="email" placeholder="E-posta" />
        <textarea placeholder="Mesajınız"></textarea>
        <button type="submit" className="btn btn-primary">Gönder</button>
      </form>
    </div>
  );
}

export default App;
