import React from 'react';
import './App.css';
import { Link } from 'react-router-dom';

const brands = [
  {
    name: 'G-TEKNİK™',
    description: 'Endüstriyel dolap, tezgah, kabin, taşıma sistemleri (PORTABOX™ uyumlu)',
  },
  {
    name: 'PORTABOX™',
    description: 'Taşınabilir plastik & alüminyum çanta sistemleri (her sisteme entegre olur)',
  },
  {
    name: 'VANCRAFT™',
    description: 'Araç içi mobil servis sistemleri (PORTABOX ile tam uyumlu)',
  },
  {
    name: 'G‑RACK™',
    description: 'Ağır yük raf sistemleri ve dış mekân çözümleri (galvaniz, cantilever)',
  },
  {
    name: 'G‑RACK CLAD™',
    description: 'Rafla entegre barınak ve depo çözümleri',
  },
  {
    name: 'G‑RACK MOBILITY™',
    description: 'Raylı, sürgülü mobil raf sistemleri (soğuk hava ve dar alan çözümleri)',
  },
  {
    name: 'G‑MEZZ™',
    description: 'ResinDek zeminli çok katlı platform sistemleri (AGV/ASRS uyumlu)',
  },
  {
    name: 'G‑TOWER™',
    description: 'Dikey otomatik raf sistemleri (PLC, HMI, Loadcell, ERP bağlantılı)',
  },
  {
    name: 'RAFKAT USTA™',
    description: 'Ekonomik, hızlı teslimatlı, taksitli raf çözümleri (e-ticaret uyumlu)',
  },
  {
    name: 'RACK CLAD PRO™',
    description: 'Anahtar teslim depo projeleri (statik + çatı + raf + platform)',
  },
  {
    name: 'G‑BOTICS™',
    description: 'AGV, ASRS, otomatik yükleme robotları (G‑TOWER ve G‑MEZZ ile entegre)',
  },
  {
    name: 'AGRORACK PRO™',
    description: 'Dikey tarım sistemleri ve şehir içi üretim modülleri',
  },
];

export default function Products() {
  return (
    <main className="products-page">
      <h1>Ürünlerimiz & Markalarımız</h1>
      <p className="products-intro">Gökdoğan çatısı altında tüm markalar, modüler tasarım prensibiyle yapılandırılmıştır. Her ürün birbiriyle entegre olabilir, yeni çözümler üretilebilir.</p>
      <div className="brands-grid">
        {brands.map((brand, idx) => (
          <div className="brand-card" key={idx}>
            <h2>{brand.name}</h2>
            <p>{brand.description}</p>
          </div>
        ))}
      </div>
      {/* Ürün Detayları */}
      <section className="product-details-showcase">
        {/* G-TOWER */}
        <Link to="/products/g-tower" className="product-card-link">
          <div className="product-card">
            <h2>G‑TOWER™</h2>
            <p>Akıllı Dikey Otomatik Raf Sistemleri. Yerli üretim, ileri teknoloji ve güvenilir çözümler.</p>
            <p className="product-card-more">Detayları Gör →</p>
          </div>
        </Link>
        {/* G-MEZZ */}
        <Link to="/products/g-mezz" className="product-card-link">
          <div className="product-card">
            <h2>G‑MEZZ™</h2>
            <p>Yeni Nesil Çok Katlı Depolama Sistemleri. Endüstriyel depolama alanınızı maksimum verimlilik ve akıllı operasyon merkezi haline getirin.</p>
            <p className="product-card-more">Detayları Gör →</p>
          </div>
        </Link>
      </section>
    </main>
  );
}
